import React from "react";
import QuoteCard from "./components/QuoteCard";

function App() {
  const [showSimpsonsOnly, setShowSimpsonsOnly] = React.useState(false);
  const [quoteList, setQuoteList] = React.useState("");

  function handleShowSimpsonsOnlyClick() {
    setShowSimpsonsOnly(!showSimpsonsOnly);
  }

  return (
    <div>
      <button onClick={handleShowSimpsonsOnlyClick}>
        Simpson family only: {showSimpsonsOnly ? "ON" : "OFF"}
      </button>
      {quoteList
        .filter((quote) =>
          showSimpsonsOnly ? quote.character.includes("Simpson") : quote
        )
        .map((quote, index) => (
          <QuoteCard key={index} {...quote} />
        ))}
    </div>
  );
}

export default App;
